import React, { useEffect } from 'react';
import "./App.css";
import Loading from "./Loading";

const { kakao } = window;
const { what3words } = window;

const Map = () => {

    useEffect(() => {
        const container = document.getElementById('myMap');
		const options = {
			center: new kakao.maps.LatLng(35.88824182876274, 128.6112113456349),
			level: 3
		};
        const map = new kakao.maps.Map(container, options);
        // 지도를 클릭한 위치에 표출할 마커입니다
        var marker = new kakao.maps.Marker({
            // 지도 중심좌표에 마커를 생성합니다 
            position: map.getCenter()
        });
        // 지도에 마커를 표시합니다
        marker.setMap(map);

        // 지도에 클릭 이벤트를 등록합니다
        // 지도를 클릭하면 마지막 파라미터로 넘어온 함수를 호출합니다
        kakao.maps.event.addListener(map, 'click', function (mouseEvent) {

        // 클릭한 위도, 경도 정보를 가져옵니다 
        var latlng = mouseEvent.latLng;

        // 마커 위치를 클릭한 위치로 옮깁니다
        marker.setPosition(latlng);

        var word_address;
        what3words.api.convertTo3wa({ lat: latlng.getLat(), lng: latlng.getLng() }, 'ko')
            .then(function (response) {
                word_address = response.words;
                var result = document.getElementById('words');
                result.innerHTML = '/// ' + word_address;
        });

        });
    }, []);

    return (
        <div className="ContentBox">
            <div id="myMap" style={{width:"100%", height:"88%"}}></div>
            <h2 id="words"><br></br></h2>
            <button id="changeButton" onClick="">변환하기 !</button>
        </div>
    );
}

export default Map;
